pragma solidity ^0.6.0;

import "./DeflyNFT.sol";
import "./DeflyToken.sol";

contract DeflyMarketplace {
    DeflyToken private _token;
    DeflyNFT private _defly;

    address private _organiser;

    constructor(DeflyToken token, DeflyNFT defly) public {
        _token = token;
        _defly = defly;
        _organiser = _defly.getOrganiser();
    }

    event Purchase(address indexed buyer, address seller, uint256 nftId);

    // Purchase nfts from the organiser directly
    function purchaseNFT() public {
        address buyer = msg.sender;

        _token.transferFrom(buyer, _organiser, _defly.getNFTPrice());

        _defly.transferNFT(buyer);
    }

    // Purchase nft from the secondary market hosted by organiser
    function secondaryPurchase(uint256 nftId) public {
        address seller = _defly.ownerOf(nftId);
        address buyer = msg.sender;
        uint256 sellingPrice = _defly.getSellingPrice(nftId);
        uint256 commision = (sellingPrice * 10) / 100;

        _token.transferFrom(buyer, seller, sellingPrice - commision);
        _token.transferFrom(buyer, _organiser, commision);

        _defly.secondaryTransferNFT(buyer, nftId);

        emit Purchase(buyer, seller, nftId);
    }
}
