pragma solidity ^0.6.0;

import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

contract DeflyNFT is Context, AccessControl, ERC721 {
    using Counters for Counters.Counter;

    Counters.Counter private _nftIds;
    Counters.Counter private _saleNftId;

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

    struct NFTDetails {
        uint256 purchasePrice;
        uint256 sellingPrice;
        bool forSale;
    }

    address private _organiser;
    address[] private customers;
    uint256[] private nftsForSale;
    uint256 private _nftPrice;
    uint256 private _totalSupply;
    string private _fileUrl;

    mapping(uint256 => NFTDetails) private _nftDetails;
    mapping(address => uint256[]) private purchasedNfts;

    constructor(
        string memory deflyName,
        string memory DeflySymbol,
        uint256 nftPrice,
        uint256 totalSupply,
        address organiser,
        string memory fileUrl
    ) public ERC721(deflyName, DeflySymbol) {
        _setupRole(MINTER_ROLE, organiser);
        _nftPrice = nftPrice;
        _totalSupply = totalSupply;
        _organiser = organiser;
        _fileUrl = fileUrl;
    }

    modifier isValidNFTCount {
        require(
            _nftIds.current() < _totalSupply,
            "Maximum nft limit exceeded!"
        );
        _;
    }

    modifier isMinterRole {
        require(
            hasRole(MINTER_ROLE, _msgSender()),
            "User must have minter role to mint"
        );
        _;
    }

    modifier isValidSellAmount(uint256 nftId) {
        uint256 purchasePrice = _nftDetails[nftId].purchasePrice;
        uint256 sellingPrice = _nftDetails[nftId].sellingPrice;

        require(
            purchasePrice + ((purchasePrice * 110) / 100) > sellingPrice,
            "Re-selling price is more than 110%"
        );
        _;
    }

    /*
     * Mint new nfts and assign it to operator
     * Access controlled by minter only
     * Returns new nftId
     */
    function mint(address operator)
        internal
        virtual
        isMinterRole
        returns (uint256)
    {
        _nftIds.increment();
        uint256 newnftId = _nftIds.current();
        _mint(operator, newnftId);
        _setTokenURI(newnftId, _fileUrl);

        _nftDetails[newnftId] = NFTDetails({
            purchasePrice: _nftPrice,
            sellingPrice: 0,
            forSale: false
        });

        return newnftId;
    }

    /*
     * Bulk mint specified number of nfts to assign it to a operator
     * Modifier to check the nft count is less than total supply
     */
    function bulkMintNFTs(uint256 numOfNfts, address operator)
        public
        virtual
        isValidNFTCount
    {
        require(
            (nftCounts() + numOfNfts) <= 1000,
            "Number of nfts exceeds maximum nft count"
        );

        for (uint256 i = 0; i < numOfNfts; i++) {
            mint(operator);
        }
    }

    /*
     * Primary purchase for the nfts
     * Adds new customer if not exists
     * Adds buyer to nfts mapping
     * Update nft details
     */
    function transferNFT(address buyer) public {
        _saleNftId.increment();
        uint256 salenftId = _saleNftId.current();

        require(
            msg.sender == ownerOf(salenftId),
            "Only initial purchase allowed"
        );

        transferFrom(ownerOf(salenftId), buyer, salenftId);

        if (!isCustomerExist(buyer)) {
            customers.push(buyer);
        }
        purchasedNfts[buyer].push(salenftId);
    }

    /*
     * Secondary purchase for the nfts
     * Modifier to validate that the selling price shouldn't exceed 110% of purchase price for peer to peer transfers
     * Adds new customer if not exists
     * Adds buyer to nfts mapping
     * Remove nft from the seller and from sale
     * Update nft details
     */
    function secondaryTransferNFT(address buyer, uint256 salenftId)
        public
        isValidSellAmount(salenftId)
    {
        address seller = ownerOf(salenftId);
        uint256 sellingPrice = _nftDetails[salenftId].sellingPrice;

        transferFrom(seller, buyer, salenftId);

        if (!isCustomerExist(buyer)) {
            customers.push(buyer);
        }

        purchasedNfts[buyer].push(salenftId);

        removeNFTFromCustomer(seller, salenftId);
        removeNFTFromSale(salenftId);

        _nftDetails[salenftId] = NFTDetails({
            purchasePrice: sellingPrice,
            sellingPrice: 0,
            forSale: false
        });
    }

    /*
     * Add nft for sale with its details
     * Validate that the selling price shouldn't exceed 110% of purchase price
     * Organiser can not use secondary market sale
     */
    function setSaleDetails(
        uint256 nftId,
        uint256 sellingPrice,
        address operator
    ) public {
        uint256 purchasePrice = _nftDetails[nftId].purchasePrice;

        require(
            purchasePrice + ((purchasePrice * 110) / 100) > sellingPrice,
            "Re-selling price is more than 110%"
        );

        // Should not be an organiser
        require(
            !hasRole(MINTER_ROLE, _msgSender()),
            "Functionality only allowed for secondary market"
        );

        _nftDetails[nftId].sellingPrice = sellingPrice;
        _nftDetails[nftId].forSale = true;

        if (!isSaleNFTAvailable(nftId)) {
            nftsForSale.push(nftId);
        }

        approve(operator, nftId);
    }

    // Get nft actual price
    function getNFTPrice() public view returns (uint256) {
        return _nftPrice;
    }

    // Get organiser's address
    function getOrganiser() public view returns (address) {
        return _organiser;
    }

    // Get current nftId
    function nftCounts() public view returns (uint256) {
        return _nftIds.current();
    }

    // Get next sale nftId
    function getNextSalenftId() public view returns (uint256) {
        return _saleNftId.current();
    }

    // Get selling price for the nft
    function getSellingPrice(uint256 nftId) public view returns (uint256) {
        return _nftDetails[nftId].sellingPrice;
    }

    // Get all nfts available for sale
    function getNFTsForSale() public view returns (uint256[] memory) {
        return nftsForSale;
    }

    // Get nft details
    function getNFTDetails(uint256 nftId)
        public
        view
        returns (
            uint256 purchasePrice,
            uint256 sellingPrice,
            bool forSale
        )
    {
        return (
            _nftDetails[nftId].purchasePrice,
            _nftDetails[nftId].sellingPrice,
            _nftDetails[nftId].forSale
        );
    }

    // Get all nfts owned by a customer
    function getNFTsOfCustomer(address customer) public view returns (uint256[] memory){
        return purchasedNfts[customer];
    }

    // Utility function to check if customer exists to avoid redundancy
    function isCustomerExist(address buyer) internal view returns (bool) {
        for (uint256 i = 0; i < customers.length; i++) {
            if (customers[i] == buyer) {
                return true;
            }
        }
        return false;
    }

    // Utility function used to check if nft is already for sale
    function isSaleNFTAvailable(uint256 nftId)
        internal
        view
        returns (bool)
    {
        for (uint256 i = 0; i < nftsForSale.length; i++) {
            if (nftsForSale[i] == nftId) {
                return true;
            }
        }
        return false;
    }

    // Utility function to remove nft owned by customer from customer to nft mapping
    function removeNFTFromCustomer(address customer, uint256 nftId)
        internal
    {
        uint256 numOfNfts = purchasedNfts[customer].length;

        for (uint256 i = 0; i < numOfNfts; i++) {
            if (purchasedNfts[customer][i] == nftId) {
                for (uint256 j = i + 1; j < numOfNfts; j++) {
                    purchasedNfts[customer][j - 1] = purchasedNfts[
                        customer
                    ][j];
                }
                purchasedNfts[customer].pop();
            }
        }
    }

    // Utility function to remove nft from sale list
    function removeNFTFromSale(uint256 nftId) internal {
        uint256 numOfNfts = nftsForSale.length;
        for (uint256 i = 0; i < numOfNfts; i++) {
            if (nftsForSale[i] == nftId) {
                for (uint256 j = i + 1; j < numOfNfts; j++) {
                    nftsForSale[j - 1] = nftsForSale[j];
                }
                nftsForSale.pop();
            }
        }
    }
}
